
public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=30;
		System.out.println("before swapping the number the value of a is="+a);
		System.out.println("before swapping the number the value of b is="+b);
		int c=a;
		a=b;
		b=c;
		System.out.println("after swapping the number the value of a is="+a);
		System.out.println("after swapping the number the value of c is="+b);
		

	}

}
